#-----------------创建表--- kj_meal_shop
DROP TABLE IF EXISTS `{DB_PRE}meal_shop`;
CREATE TABLE IF NOT EXISTS `{DB_PRE}meal_shop` (
`shop_id` int(10) NOT NULL auto_increment,`shop_name` varchar(50) NOT NULL COMMENT '店铺名称',`shop_user_id` int(10) NOT NULL COMMENT '用户id',`shop_addtime` int(10) NOT NULL COMMENT '添加时间',`shop_updatetime` int(10) NOT NULL COMMENT '修改时间',`shop_opentime_start` int(10) NOT NULL COMMENT '营业开始时间',`shop_opentime_close` int(10) NOT NULL COMMENT '营业结束时间',`shop_intro` text NOT NULL COMMENT '介绍',`shop_linkname` varchar(50) NOT NULL COMMENT '联系人',`shop_linktel` varchar(50) NOT NULL COMMENT '联系人电话',`shop_tel` varchar(20) NOT NULL COMMENT '店铺电话',`shop_address` varchar(100) NOT NULL COMMENT '店铺地址',`shop_area_id` int(10) NOT NULL COMMENT '地区id',`shop_area_allid` varchar(50) NOT NULL COMMENT '地区id全路径',`shop_area` varchar(50) NOT NULL COMMENT '所在区域名',`shop_oneminleast` decimal(10,2) NOT NULL COMMENT '单份最低价',`shop_dispatch_price` decimal(10,2) NOT NULL COMMENT '起送价格',`shop_state` smallint(2) NOT NULL COMMENT '状态',`shop_pic_small` varchar(100) NOT NULL COMMENT '店面logo',`shop_pic` varchar(100) NOT NULL COMMENT '小图',`shop_mode` smallint(2) NOT NULL COMMENT '经营范围',`shop_dispatch` text NOT NULL COMMENT '送货信息',`shop_extend` text NOT NULL,`shop_ticket` tinyint(1) NOT NULL COMMENT '供提发票',`shop_progress` varchar(50) NOT NULL COMMENT '店铺进度',`shop_dispatch_mode` smallint(2) NOT NULL COMMENT '送餐范围模式',`shop_hits` int(10) NOT NULL COMMENT '店铺访问量',`shop_menu_num` int(10) NOT NULL COMMENT '店铺菜品数',`shop_isdel` tinyint(1) default '0' COMMENT '回收站',`shop_rebate` int(2) NOT NULL COMMENT '销售返点',`shop_checkout_date` date NOT NULL COMMENT '上次结款日',`shop_checkout_money` int(10) NOT NULL COMMENT '最低结款额',`shop_sms` smallint(2) NOT NULL COMMENT '信短通知级别',`shop_sms_tel` varchar(100) NOT NULL COMMENT '订餐短信电话',`shop_printinfo` text NOT NULL COMMENT '打印格式信息',`shop_support_score` tinyint(1) default '0' COMMENT '是否支持积分兑换',`shop_tj` int(5) default '0' COMMENT '推荐',`shop_jian` varchar(20) NOT NULL COMMENT '拼音简写',`shop_desc` varchar(500) NOT NULL COMMENT '简介',`shop_sold` int(10) default '0' COMMENT '总销量',`shop_addprice` int(10) default '0' COMMENT '送配费',`shop_type` varchar(20) NOT NULL COMMENT ' 分类',`shop_verifytel` tinyint(1) default '0' COMMENT '下单是否短信认证',`shop_email` varchar(50) NOT NULL COMMENT '新单是否邮件提醒',`shop_weixin_id` varchar(50) NOT NULL COMMENT '微信Id',`shop_print_id` varchar(20) NOT NULL COMMENT '打印机号',`shop_print_auto` tinyint(1) default '0' COMMENT '自动打印',`shop_print_cancel` tinyint(1) default '0' COMMENT '取消是否打印',`shop_print_tongbu` tinyint(1) default '0',`shop_position_lng` double(11,6) default '0.000000',`shop_position_lat` double(11,6) default '0.000000',`shop_distance` int(11) default '0',`shop_position_max_lat` decimal(20,10) default '0.0000000000',`shop_position_min_lat` decimal(20,10) default '0.0000000000',`shop_position_max_lng` decimal(20,10) default '0.0000000000',`shop_position_min_lng` decimal(20,10) default '0.0000000000',`shop_comment_list` varchar(100) NOT NULL,`shop_comment_val` varchar(100) NOT NULL,`shop_comment` decimal(2,1) default '0.0',`shop_position_precision` double(11,6) default '0.000000',`shop_commentnum` int(10) default '0',`shop_day_limit` int(10) default '0' COMMENT '当天销量限制',`shop_order_time` int(10) default '0' COMMENT '最后下单时间',`shop_day_sold` int(10) default '0' COMMENT '当天销量',`shop_print_pages` tinyint(1) default '0',`shop_comment_listall` varchar(100) NOT NULL,`shop_close_tips` varchar(255) NOT NULL COMMENT '停业公告',`shop_tips` varchar(255) NOT NULL,`shop_service` tinyint(1) default '0',`shop_reserve_time` int(10) default '0' COMMENT '订位间隔(分)',`shop_reserve_money` decimal(10,2) default '0.00' COMMENT '订位预付订金',`shop_menutime` int(10) default '0' COMMENT '上菜时间',`shop_capitaprice` int(10) default '0' COMMENT '人均消费',`shop_reserve_sms` tinyint(4) default '0' COMMENT '短信验证',PRIMARY KEY (`shop_id`)
) ENGINE=myisam  DEFAULT CHARSET=utf8 AUTO_INCREMENT=1

